import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { addProperty } from '../services/api';
import './AddProperty.css';

function AddProperty() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    location: '',
    bedrooms: '',
    bathrooms: '',
    furnished: false,
    amenities: [],
    images: [],
    video: null
  });

  const handleInputChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: type === 'checkbox' ? checked : 
               type === 'file' ? (name === 'images' ? Array.from(files) : files[0]) :
               value
    }));
  };

  const handleAmenityChange = (e) => {
    const { value, checked } = e.target;
    setFormData(prevState => ({
      ...prevState,
      amenities: checked
        ? [...prevState.amenities, value]
        : prevState.amenities.filter(amenity => amenity !== value)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formDataToSend = new FormData();
      Object.keys(formData).forEach(key => {
        if (key === 'images') {
          formData[key].forEach(image => formDataToSend.append('images', image));
        } else if (key === 'amenities') {
          formDataToSend.append('amenities', JSON.stringify(formData[key]));
        } else {
          formDataToSend.append(key, formData[key]);
        }
      });

      await addProperty(formDataToSend);
      alert('Property added successfully!');
      navigate('/view-all-rooms');
    } catch (error) {
      console.error('Error adding property:', error);
      alert('Failed to add property. Please try again.');
    }
  };

  return (
    <div className="add-property-page">
      <h1>Add Your Room</h1>
      <form onSubmit={handleSubmit} className="add-property-form">
        <div className="form-group">
          <label htmlFor="title">Room Title</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            required
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="price">Price (Rs. per month)</label>
          <input
            type="number"
            id="price"
            name="price"
            value={formData.price}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="location">Location</label>
          <input
            type="text"
            id="location"
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="bedrooms">Number of Bedrooms</label>
          <input
            type="number"
            id="bedrooms"
            name="bedrooms"
            value={formData.bedrooms}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="bathrooms">Number of Bathrooms</label>
          <input
            type="number"
            id="bathrooms"
            name="bathrooms"
            value={formData.bathrooms}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label>
            <input
              type="checkbox"
              name="furnished"
              checked={formData.furnished}
              onChange={handleInputChange}
            />
            Furnished
          </label>
        </div>
        <div className="form-group">
          <label>Amenities</label>
          <div className="amenities-group">
            <label>
              <input
                type="checkbox"
                name="amenities"
                value="wifi"
                checked={formData.amenities.includes('wifi')}
                onChange={handleAmenityChange}
              />
              Wi-Fi
            </label>
            <label>
              <input
                type="checkbox"
                name="amenities"
                value="parking"
                checked={formData.amenities.includes('parking')}
                onChange={handleAmenityChange}
              />
              Parking
            </label>
            <label>
              <input
                type="checkbox"
                name="amenities"
                value="ac"
                checked={formData.amenities.includes('ac')}
                onChange={handleAmenityChange}
              />
              Air Conditioning
            </label>
          </div>
        </div>
        <div className="form-group">
          <label htmlFor="images">Upload Images</label>
          <input
            type="file"
            id="images"
            name="images"
            accept="image/*"
            multiple
            onChange={handleInputChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="video">Upload Video</label>
          <input
            type="file"
            id="video"
            name="video"
            accept="video/*"
            onChange={handleInputChange}
          />
        </div>
        <div className="button-group">
          <button type="submit" className="btn btn-primary">Add Property</button>
        </div>
      </form>
    </div>
  );
}

export default AddProperty;
