import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import { Edit, Trash2 } from 'lucide-react';
import './ManageProperty.css';

function ManageProperties() {
  const [properties, setProperties] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchProperties();
  }, []);

  const fetchProperties = async () => {
    setIsLoading(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const response = await axios.get('http://localhost:5000/api/user/properties', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setProperties(response.data);
      setIsLoading(false);
    } catch (error) {
      console.error('Error fetching properties:', error);
      setError('Failed to fetch properties. Please try again.');
      setIsLoading(false);
    }
  };

  const handleEditProperty = (propertyId) => {
    navigate(`/edit-property/${propertyId}`);
  };

  const handleDeleteProperty = async (propertyId) => {
    if (window.confirm('Are you sure you want to delete this property?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:5000/api/properties/${propertyId}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        setProperties(properties.filter(prop => prop._id !== propertyId));
      } catch (error) {
        console.error('Error deleting property:', error);
        setError('Failed to delete property. Please try again.');
      }
    }
  };

  if (isLoading) {
    return <div className="container mt-5">Loading...</div>;
  }

  if (error) {
    return <div className="container mt-5 text-danger">{error}</div>;
  }

  return (
    <div className="manage-properties-page">
      <div className="container">
        <h1 className="page-title">Manage Your Properties</h1>
        {properties.length === 0 ? (
          <p>You haven't added any properties yet. <Link to="/add-property">Add a property</Link></p>
        ) : (
          <div className="properties-grid">
            {properties.map((property) => (
              <div key={property._id} className="property-card">
                <img 
                  src={property.images[0] || '/placeholder.jpg'} 
                  alt={property.title}
                  className="property-image"
                />
                <div className="property-details">
                  <h3 className="property-title">{property.title}</h3>
                  <p className="property-location">{property.location}</p>
                  <p className="property-price">Rs {property.price.toLocaleString()}/month</p>
                  <div className="property-actions">
                    <button 
                      onClick={() => handleEditProperty(property._id)} 
                      className="btn btn-primary"
                    >
                      <Edit size={16} /> Edit
                    </button>
                    <button 
                      onClick={() => handleDeleteProperty(property._id)} 
                      className="btn btn-danger"
                    >
                      <Trash2 size={16} /> Delete
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default ManageProperties;
