import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './ViewAllRoom.css';

function ViewAllRooms() {
    const [properties, setProperties] = useState([]);

    useEffect(() => {
        const fetchProperties = async () => {
            try {
                const response = await fetch('http://localhost:5004/api/properties');
                if (response.ok) {
                    const data = await response.json();
                    setProperties(data);
                } else {
                    console.error('Failed to fetch properties.');
                }
            } catch (error) {
                console.error('Error fetching properties:', error);
            }
        };

        fetchProperties();
    }, []);

    return (
        <div className="view-all-rooms-page">
            <h1>All Properties</h1>
            <div className="properties-list">
                {properties.map((property, index) => (
                    <div key={index} className="property-card">
                        <h2>{property.title}</h2>
                        <p>{property.description}</p>
                        <p>Price: Rs. {property.price}</p>
                        <p>Location: {property.location}</p>
                        <p>Bedrooms: {property.bedrooms}</p>
                        <p>Bathrooms: {property.bathrooms}</p>
                        <p>Furnished: {property.furnished ? 'Yes' : 'No'}</p>
                        <p>Amenities: {property.amenities.join(', ')}</p>
                    </div>
                ))}
            </div>
            <Link to="/add-property" className="btn btn-primary">
                Add a New Property
            </Link>
        </div>
    );
}

export default ViewAllRooms;
