import React, { useState, useRef, useEffect } from 'react';
import { Bell } from 'lucide-react';

const mockNotifications = [
  { id: '1', title: 'New Message', message: 'You have a new message from your landlord.', time: '5 minutes ago' },
  { id: '2', title: 'Rent Due', message: 'Your rent is due in 3 days.', time: '2 hours ago' },
  { id: '3', title: 'Maintenance Update', message: 'The scheduled maintenance has been completed.', time: '1 day ago' },
];

export function NotificationButton() {
  const [notifications, setNotifications] = useState(mockNotifications);
  const [isOpen, setIsOpen] = useState(false);
  const popoverRef = useRef(null);

  const markAsRead = (id) => {
    setNotifications(notifications.filter(n => n.id !== id));
  };

  useEffect(() => {
    function handleClickOutside(event) {
      if (popoverRef.current && !popoverRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    }

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="relative inline-block">
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="p-2 rounded-md border border-gray-300 text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
      >
        <Bell className="h-5 w-5" />
        {notifications.length > 0 && (
          <span className="absolute top-0 right-0 h-2 w-2 rounded-full bg-red-500" />
        )}
      </button>
      {isOpen && (
        <div
          ref={popoverRef}
          className="absolute z-10 mt-2 w-80 bg-white rounded-md shadow-lg"
          style={{ right: 0 }}
        >
          <div className="p-4">
            <h3 className="font-semibold mb-2">Notifications</h3>
            {notifications.length === 0 ? (
              <p className="text-sm text-gray-500">No new notifications</p>
            ) : (
              <div className="overflow-auto" style={{ maxHeight: '300px' }}>
                {notifications.map((notification) => (
                  <div key={notification.id} className="mb-4 last:mb-0">
                    <div className="flex justify-between items-start">
                      <h4 className="text-sm font-semibold">{notification.title}</h4>
                      <button 
                        onClick={() => markAsRead(notification.id)}
                        className="text-xs text-gray-500 hover:text-gray-700"
                      >
                        Mark as read
                      </button>
                    </div>
                    <p className="text-sm">{notification.message}</p>
                    <p className="text-xs text-gray-500 mt-1">{notification.time}</p>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
