import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './components/Home';
import Services from './components/Services';
import About from './components/About';
import AddProperty from './components/AddProperty';
import Login from './components/Login';
import Register from './components/Register';
import BookNow from './components/BookNow';
import ViewAllRooms from './components/ViewAllRoom';
import Saved from './components/Saved';
import ManageProperties from './components/ManageProperty';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (token && storedUser) {
      setIsAuthenticated(true);
      setUser(storedUser);
    }
  }, []);

  const login = (userData, token) => {
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(userData));
    setIsAuthenticated(true);
    setUser(userData);
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsAuthenticated(false);
    setUser(null);
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home isAuthenticated={isAuthenticated} user={user} logout={logout} />} />
        <Route path="/services" element={<Services />} />
        <Route path="/about" element={<About />} />
        <Route path="/login" element={<Login login={login} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/booknow/:id" element={<BookNow isAuthenticated={isAuthenticated} user={user} />} />
        <Route path="/rooms" element={<ViewAllRooms isAuthenticated={isAuthenticated} user={user} />} />
        <Route path="/saved" element={<Saved isAuthenticated={isAuthenticated} user={user} />} />

        <Route 
          path="/add-property" 
          element={
            isAuthenticated ? 
            <AddProperty user={user} /> : 
            <Navigate to="/login" replace state={{ from: '/add-property' }} />
          } 
        />
      </Routes>
    </Router>
  );
}

export default App;
