import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { getProperties } from '../services/api';
import './ViewAllRoom.css';

function ViewAllRooms() {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchProperties = useCallback(async () => {
    try {
      setLoading(true);
      const response = await getProperties();
      setProperties(response.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching properties:', err);
      setError('Failed to load properties. Please try again later.');
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchProperties();
  }, [fetchProperties]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="view-all-rooms-page">
      <h1>All Available Rooms</h1>
      <button onClick={fetchProperties} className="refresh-button">Refresh</button>
      {properties.length === 0 ? (
        <p>No properties available at the moment.</p>
      ) : (
        <div className="property-list">
          {properties.map((property) => (
            <div key={property._id} className="property-card">
              {property.images && property.images.length > 0 && (
                <img src={property.images[0]} alt={property.title} className="property-image" />
              )}
              <div className="property-details">
                <h2>{property.title}</h2>
                <p>{property.description}</p>
                <p>Price: Rs. {property.price} per month</p>
                <p>Location: {property.location}</p>
                <p>Bedrooms: {property.bedrooms}</p>
                <p>Bathrooms: {property.bathrooms}</p>
                <p>{property.furnished ? 'Furnished' : 'Unfurnished'}</p>
                <div className="amenities">
                  {property.amenities.map((amenity, index) => (
                    <span key={index}>{amenity}</span>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
      <Link to="/add-property" className="add-property-button">Add New Property</Link>
    </div>
  );
}

export default ViewAllRooms;
