import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './AddProperty.css';

function AddProperty() {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    location: '',
    bedrooms: '',
    bathrooms: '',
    furnished: false,
    amenities: [],
    imagePaths: [],
    videoPath: ''
  });
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    } else {
      setIsAuthenticated(true);
    }
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleAmenityChange = (e) => {
    const { value, checked } = e.target;
    setFormData(prevState => ({
      ...prevState,
      amenities: checked
        ? [...prevState.amenities, value]
        : prevState.amenities.filter(amenity => amenity !== value)
    }));
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const paths = files.map(file => URL.createObjectURL(file));
    setFormData(prevState => ({
      ...prevState,
      imagePaths: [...prevState.imagePaths, ...paths]
    }));
  };

  const handleVideoUpload = (e) => {
    const file = e.target.files[0];
    const path = URL.createObjectURL(file);
    setFormData(prevState => ({
      ...prevState,
      videoPath: path
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/properties', formData, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });
      console.log('Property added:', response.data);
      alert('Property added successfully!');
      navigate('/view-all-rooms');
    } catch (error) {
      console.error('Error adding property:', error);
      alert(`Failed to add property. Error: ${error.response?.data?.message || error.message}`);
    }
  };

  if (!isAuthenticated) {
    return null;
  }

  return (
    <div className="add-property-page">
      <header className="header">
        <h1>Add Your Property</h1>
      </header>

      <main className="add-property-content">
        <div className="container">
          <form onSubmit={handleSubmit} className="add-property-form">
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="description">Description</label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="price">Price (per month)</label>
              <input
                type="number"
                id="price"
                name="price"
                value={formData.price}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="location">Location</label>
              <input
                type="text"
                id="location"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="bedrooms">Bedrooms</label>
              <input
                type="number"
                id="bedrooms"
                name="bedrooms"
                value={formData.bedrooms}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="bathrooms">Bathrooms</label>
              <input
                type="number"
                id="bathrooms"
                name="bathrooms"
                value={formData.bathrooms}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="furnished">Furnished</label>
              <input
                type="checkbox"
                id="furnished"
                name="furnished"
                checked={formData.furnished}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label>Amenities</label>
              <div className="amenities-group">
                {['AC', 'WiFi', 'Parking', 'Water', 'TV', 'Washing Machine', 'Refrigerator', 'Microwave'].map((amenity) => (
                  <div key={amenity} className="amenity-item">
                    <input
                      type="checkbox"
                      id={amenity.toLowerCase()}
                      name="amenities"
                      value={amenity.toLowerCase()}
                      checked={formData.amenities.includes(amenity.toLowerCase())}
                      onChange={handleAmenityChange}
                    />
                    <label htmlFor={amenity.toLowerCase()}>{amenity}</label>
                  </div>
                ))}
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="images">Upload Images</label>
              <input
                type="file"
                id="images"
                name="images"
                accept="image/*"
                multiple
                onChange={handleImageUpload}
              />
              {formData.imagePaths.map((path, index) => (
                <div key={index}>
                  <img src={path} alt={`Uploaded ${index + 1}`} style={{width: '100px', height: '100px'}} />
                  <p>Image Path: {path}</p>
                </div>
              ))}
            </div>
            <div className="form-group">
              <label htmlFor="video">Upload Video</label>
              <input
                type="file"
                id="video"
                name="video"
                accept="video/*"
                onChange={handleVideoUpload}
              />
              {formData.videoPath && (
                <div>
                  <video src={formData.videoPath} style={{width: '200px'}} controls />
                  <p>Video Path: {formData.videoPath}</p>
                </div>
              )}
            </div>
            <div className="button-group">
              <button type="submit" className="btn btn-primary">Add Property</button>
            </div>
          </form>
        </div>
      </main>

      <footer className="footer">
        <p>&copy; 2024 RoomRental. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default AddProperty;
