import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './ViewRooms.css';

const rooms = [
  // Add the list of rooms here (can be fetched dynamically)
  {
    id: 1,
    title: "Spacious room in Thamel",
    price: 25000,
    furnished: true,
    amenities: ["Parking", "WiFi", "Water"],
    image: "/room1.jpg",
  },
  {
    id: 2,
    title: "Spacious Apartment in Patan",
    price: 35000,
    furnished: false,
    amenities: ["Parking", "WiFi"],
    image: "/room2.jpeg",
  },
  {
    id: 3,
    title: "Modern Loft in Lazimpat",
    price: 30000,
    furnished: true,
    amenities: ["WiFi", "Water"],
    image: "/room3.jpg",
  },
];

function ViewRooms() {
  const [favorites, setFavorites] = useState([]);

  const toggleFavorite = (roomId) => {
    setFavorites((prev) =>
      prev.includes(roomId)
        ? prev.filter((id) => id !== roomId)
        : [...prev, roomId]
    );
  };

  return (
    <div className="view-rooms-page">
      <div className="container">
        <h1>All Available Rooms</h1>
        <div className="listings-grid">
          {rooms.map((room) => (
            <div key={room.id} className="listing-card">
              <img src={room.image} alt={room.title} className="listing-image" />
              <div className="listing-details">
                <h3>{room.title}</h3>
                <p className="listing-price">Rs {room.price.toLocaleString()}</p>
                <p className="furnished-status">
                  {room.furnished ? "Furnished" : "Unfurnished"}
                </p>
                <div className="amenities">
                  {room.amenities.map((amenity, index) => (
                    <span key={index} className="amenity">
                      {amenity}
                    </span>
                  ))}
                </div>
                <div className="listing-actions">
                  <button
                    className={`btn btn-icon ${
                      favorites.includes(room.id) ? "btn-favorite-active" : ""
                    }`}
                    onClick={() => toggleFavorite(room.id)}
                  >
                    ❤️
                  </button>
                  <Link to={`/rooms/${room.id}`} className="btn btn-primary">
                    View Details
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ViewRooms;
