import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Trash2, Edit, Plus } from 'lucide-react';
import './ManageProperty.css';

function ManageProperties() {
  const [properties, setProperties] = useState([]);

  useEffect(() => {
    const storedProperties = JSON.parse(localStorage.getItem('properties')) || [];
    setProperties(storedProperties);
  }, []);

  const handleDeleteProperty = (propertyId) => {
    const updatedProperties = properties.filter(property => property.id !== propertyId);
    setProperties(updatedProperties);
    localStorage.setItem('properties', JSON.stringify(updatedProperties));
  };

  return (
    <div className="manage-properties-page">
      <header className="header">
        <div className="container">
          <Link to="/" className="logo">
            <span className="home-icon">üè†</span>
            <span className="logo-text">RoomRental</span>
          </Link>
          <nav>
            <Link to="/" className="nav-link">Home</Link>
            <Link to="/rooms" className="nav-link">View All Rooms</Link>
          </nav>
        </div>
      </header>

      <main className="manage-properties-content">
        <div className="container">
          <h1>Manage Your Properties</h1>
          <Link to="/add-property" className="btn btn-primary add-property-btn">
            <Plus size={20} />
            Add New Property
          </Link>
          {properties.length === 0 ? (
            <p>You haven't added any properties yet.</p>
          ) : (
            <div className="property-list">
              {properties.map((property) => (
                <div key={property.id} className="property-item">
                  <img src={property.image} alt={property.title} className="property-image" />
                  <div className="property-details">
                    <h3>{property.title}</h3>
                    <p>Rs {property.price.toLocaleString()}/month</p>
                    <p>{property.furnished ? 'Furnished' : 'Unfurnished'}</p>
                  </div>
                  <div className="property-actions">
                    <Link to={`/edit-property/${property.id}`} className="btn btn-secondary">
                      <Edit size={20} />
                      Edit
                    </Link>
                    <button 
                      className="btn btn-danger"
                      onClick={() => handleDeleteProperty(property.id)}
                    >
                      <Trash2 size={20} />
                      Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </main>

      <footer className="footer">
        <div className="container">
          <p>&copy; 2024 RoomRental. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
}

export default ManageProperties;
