import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Heart, Bell, MessageCircle, User } from 'lucide-react';
import ChatBox from './ChatBox';

const Saved = () => {
  const [savedListings, setSavedListings] = useState([]);
  const [showChat, setShowChat] = useState(false);
  const [currentLandlord, setCurrentLandlord] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const [username, setUsername] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Load favorites from localStorage
    const storedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setSavedListings(storedFavorites);

    // Check login status
    const token = localStorage.getItem('token');
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (token && storedUser) {
      setIsLoggedIn(true);
      setUsername(storedUser.name);
    } else {
      setIsLoggedIn(false);
    }
  }, []);

  const removeFavorite = (listingId) => {
    setSavedListings(prev => {
      const newListings = prev.filter(listing => listing.id !== listingId);
      localStorage.setItem('favorites', JSON.stringify(newListings));
      return newListings;
    });
  };

  const handleBookNow = (listing) => {
    navigate(`/booknow/${listing.id}`, { state: { roomDetails: listing } });
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsLoggedIn(false);
    navigate('/');
  };

  return (
    <div className="saved-page">
      <header className="header">
        <div className="container">
          <Link to="/" className="logo">
            <span className="home-icon">üè†</span>
            <span className="logo-text">RoomRental</span>
          </Link>
          <nav className="main-nav">
            <ul className="nav-links">
              <li><Link to="/services" className="nav-link">Services</Link></li>
              <li><Link to="/about" className="nav-link">About Us</Link></li>
              <li><Link to="/saved" className="nav-link">Saved</Link></li>
              <li><Link to="/add-property" className="nav-link">Add Property</Link></li>
            </ul>
            <div className="nav-actions">
              <div className="nav-icons">
                <Link to="/notifications" className="icon-link">
                  <Bell size={20} />
                </Link>
                <Link to="/messages" className="icon-link">
                  <MessageCircle size={20} />
                </Link>
              </div>
              {isLoggedIn ? (
                <div className="user-menu">
                  <div className="profile-icon" onClick={() => setShowDropdown(!showDropdown)}>
                    <User size={24} />
                    <span className="username">{username}</span>
                  </div>
                  {showDropdown && (
                    <div className="dropdown-menu">
                      <Link to="/profile" className="dropdown-item">Personal Information</Link>
                      <Link to="/settings" className="dropdown-item">Settings</Link>
                      <button onClick={handleLogout} className="dropdown-item">Logout</button>
                    </div>
                  )}
                </div>
              ) : (
                <div className="auth-buttons">
                  <Link to="/login" className="btn btn-outline">Sign In</Link>
                  <Link to="/register" className="btn btn-primary">Sign Up</Link>
                </div>
              )}
            </div>
          </nav>
        </div>
      </header>

      <section className="featured-listings">
        <div className="container">
          <div className="saved-header">
            <h2>Saved Listings</h2>
            <Link to="/" className="btn btn-secondary">Back to Home</Link>
          </div>
          {savedListings.length === 0 ? (
            <p>You haven't saved any listings yet.</p>
          ) : (
            <div className="listings-grid">
              {savedListings.map((listing) => (
                <div key={listing.id} className="listing-card">
                  <img 
                    src={listing.image} 
                    alt={listing.title} 
                    className="listing-image" 
                    width={250}
                    height={167}
                  />
                  <div className="listing-details">
                    <h3>{listing.title}</h3>
                    <p className="listing-price">Rs {listing.price.toLocaleString()}/month</p>
                    <p className="furnished-status">
                      {listing.furnished ? 'Furnished' : 'Unfurnished'}
                    </p>
                    <div className="amenities">
                      {listing.amenities.parking && (
                        <div className="amenity">üÖøÔ∏è Parking</div>
                      )}
                      {listing.amenities.wifi && (
                        <div className="amenity">üì∂ WiFi</div>
                      )}
                      {listing.amenities.water && (
                        <div className="amenity">üíß Water</div>
                      )}
                    </div>
                    <div className="listing-actions">
                      <button 
                        className="btn btn-primary btn-book"
                        onClick={() => handleBookNow(listing)}
                      >
                        Book Now
                      </button>
                      <button 
                        className="btn btn-outline btn-chat"
                        onClick={() => {
                          setCurrentLandlord(listing.landlordName);
                          setShowChat(true);
                        }}
                      >
                        üí¨ Chat with Landlord
                      </button>
                      <button 
                        className="btn btn-icon btn-favorite-active"
                        onClick={() => removeFavorite(listing.id)}
                        aria-label="Remove from favorites"
                      >
                        <Heart size={20} fill="red" />
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>

      {showChat && (
        <ChatBox
          onClose={() => setShowChat(false)}
          landlordName={currentLandlord}
        />
      )}
    </div>
  );
};

export default Saved;
