import React, { useState } from 'react';
import { useParams, useLocation, useNavigate, Link } from 'react-router-dom';
import { Calendar, Users, MessageSquare, MapPin, CreditCard, CheckCircle, Home } from 'lucide-react';
import './BookNow.css';

function BookNow() {
  const { id } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const roomDetails = location.state?.roomDetails;

  const [bookingData, setBookingData] = useState({
    moveInDate: '',
    leaseDuration: '6',
    familyMembers: 1,
    message: '',
    securityDeposit: roomDetails ? roomDetails.price : 0,
  });

  const [showVideos, setShowVideos] = useState(false);

  if (!roomDetails) {
    navigate('/');
    return null;
  }

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBookingData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Booking request submitted:', { roomId: id, ...bookingData });
    alert('Your rental request has been submitted. You will be redirected to eSewa for payment.');
    window.location.href = 'https://esewa.com.np/payment/placeholder';
  };

  const openMapDirections = () => {
    const mapUrl = `https://www.google.com/maps/dir/?api=1&destination=${encodeURIComponent(roomDetails.location)}`;
    window.open(mapUrl, '_blank');
  };

  return (
    <div className="book-now-page">
      <div className="book-now-container">
        <div className="book-now-header">
          <h2>Request to Rent</h2>
          <p>Complete your rental request for {roomDetails.title}</p>
          <Link to="/" className="back-to-home-btn">
            <Home size={18} />
            Back to Home
          </Link>
        </div>
        <div className="book-now-content">
          <div className="room-details-section">
            <img src={roomDetails.image} alt={roomDetails.title} className="room-image" />
            <div className="room-info">
              <h3>{roomDetails.title}</h3>
              <p className="room-price">Rs {roomDetails.price.toLocaleString()}/month</p>
              <p className="room-status">{roomDetails.furnished ? 'Furnished' : 'Unfurnished'}</p>
              <div className="location-info">
                <MapPin size={18} />
                <span>{roomDetails.location}</span>
                <button onClick={openMapDirections} className="map-direction-btn">Get Directions</button>
              </div>
              <div className="amenities">
                <h4>Amenities:</h4>
                <ul>
                  {roomDetails.amenities.parking && <li><CheckCircle size={16} /> Parking</li>}
                  {roomDetails.amenities.wifi && <li><CheckCircle size={16} /> WiFi</li>}
                  {roomDetails.amenities.water && <li><CheckCircle size={16} /> Water</li>}
                </ul>
              </div>
            </div>
          </div>
          <div className="room-videos">
            <h4>Room Videos</h4>
            <button onClick={() => setShowVideos(!showVideos)} className="toggle-videos-btn">
              {showVideos ? 'Hide Videos' : 'Show Videos'}
            </button>
            {showVideos && (
              <div className="video-container">
                {roomDetails.videos && roomDetails.videos.map((video, index) => (
                  <div key={index} className="video-item">
                    <video controls width="100%">
                      <source src={video.url} type="video/mp4" />
                      Your browser does not support the video tag.
                    </video>
                    <p>{video.description}</p>
                  </div>
                ))}
                {(!roomDetails.videos || roomDetails.videos.length === 0) && (
                  <p>No videos available for this room.</p>
                )}
              </div>
            )}
          </div>
          <form onSubmit={handleSubmit} className="booking-form">
            <div className="form-group">
              <label htmlFor="moveInDate">Preferred Move-in Date</label>
              <div className="input-wrapper">
                <Calendar className="input-icon" size={18} />
                <input
                  type="date"
                  id="moveInDate"
                  name="moveInDate"
                  value={bookingData.moveInDate}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="leaseDuration">Lease Duration</label>
              <div className="input-wrapper">
                <select
                  id="leaseDuration"
                  name="leaseDuration"
                  value={bookingData.leaseDuration}
                  onChange={handleInputChange}
                  required
                >
                  <option value="3">3 months</option>
                  <option value="6">6 months</option>
                  <option value="12">12 months</option>
                  <option value="24">24 months</option>
                </select>
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="familyMembers">Number of Family Members</label>
              <div className="input-wrapper">
                <Users className="input-icon" size={18} />
                <input
                  type="number"
                  id="familyMembers"
                  name="familyMembers"
                  min="1"
                  value={bookingData.familyMembers}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="message">Message to Landlord</label>
              <div className="input-wrapper">
                <MessageSquare className="input-icon" size={18} />
                <textarea
                  id="message"
                  name="message"
                  rows={3}
                  value={bookingData.message}
                  onChange={handleInputChange}
                  placeholder="Introduce yourself and ask any questions you may have about the room or rental terms."
                ></textarea>
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="securityDeposit">Security Deposit (Rs)</label>
              <div className="input-wrapper">
                <CreditCard className="input-icon" size={18} />
                <input
                  type="number"
                  id="securityDeposit"
                  name="securityDeposit"
                  min="0"
                  value={bookingData.securityDeposit}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            <button type="submit" className="submit-button">
              Proceed to eSewa Payment
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default BookNow;
