import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Heart } from 'lucide-react';
import './ViewAllRoom.css';

const allRooms = [
  {
    id: 1,
    title: "Spacious room in Thamel",
    price: 25000,
    furnished: true,
    amenities: { parking: true, wifi: true, water: true },
    image: "/room1.jpg",
    landlordName: "Nifiya Shrestha",
  },
  {
    id: 2,
    title: "Spacious Apartment in Patan",
    price: 35000,
    furnished: false,
    amenities: { parking: true, wifi: true, water: false },
    image: "/room2.jpeg",
    landlordName: "Nitika Suwal",
  },
  {
    id: 3,
    title: "Modern Loft in Lazimpat",
    price: 30000,
    furnished: true,
    amenities: { parking: false, wifi: true, water: true },
    image: "/room3.jpg",
    landlordName: "Sara Lamichhane",
  },
  // Add more rooms here...
];

function ViewAllRooms() {
  const [favorites, setFavorites] = useState([]);

  const toggleFavorite = (roomId) => {
    setFavorites((prev) =>
      prev.includes(roomId) ? prev.filter((id) => id !== roomId) : [...prev, roomId]
    );
  };

  return (
    <div className="view-all-rooms-page">
      <div className="container">
        <h1>All Available Rooms</h1>
        <div className="listings-grid">
          {allRooms.map((room) => (
            <div key={room.id} className="listing-card">
              <img
                src={room.image}
                alt={room.title}
                className="listing-image"
                width={250}
                height={167}
              />
              <div className="listing-details">
                <h3>{room.title}</h3>
                <p className="listing-price">Rs {room.price.toLocaleString()}/month</p>
                <p className="furnished-status">
                  {room.furnished ? "Furnished" : "Unfurnished"}
                </p>
                <div className="amenities">
                  {room.amenities.parking && <div className="amenity">üÖøÔ∏è Parking</div>}
                  {room.amenities.wifi && <div className="amenity">üì∂ WiFi</div>}
                  {room.amenities.water && <div className="amenity">üíß Water</div>}
                </div>
                <div className="listing-actions">
                  <button
                    className={`btn btn-icon ${
                      favorites.includes(room.id) ? "btn-favorite-active" : ""
                    }`}
                    onClick={() => toggleFavorite(room.id)}
                  >
                    <Heart size={20} />
                  </button>
                  <Link to={`/rooms/${room.id}`} className="btn btn-primary">
                    View Details
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ViewAllRooms;
