import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './AddProperty.css';

function AddProperty() {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        price: '',
        location: '',
        bedrooms: '',
        bathrooms: '',
        furnished: false,
        amenities: [],
        images: [],
        video: null,
    });

    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData((prevState) => ({
            ...prevState,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleAmenityChange = (e) => {
        const { value, checked } = e.target;
        setFormData((prevState) => ({
            ...prevState,
            amenities: checked
                ? [...prevState.amenities, value]
                : prevState.amenities.filter((amenity) => amenity !== value),
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5004/api/properties', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData),
            });
            if (response.ok) {
                alert('Property added successfully!');
                setFormData({
                    title: '',
                    description: '',
                    price: '',
                    location: '',
                    bedrooms: '',
                    bathrooms: '',
                    furnished: false,
                    amenities: [],
                    images: [],
                    video: null,
                });
            } else {
                alert('Failed to add property.');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('An error occurred.');
        }
    };

    return (
        <div className="add-property-page">
            <h1>Add Your Room</h1>
            <form onSubmit={handleSubmit} className="add-property-form">
                <input
                    type="text"
                    name="title"
                    placeholder="Room Title"
                    value={formData.title}
                    onChange={handleInputChange}
                    required
                />
                <textarea
                    name="description"
                    placeholder="Description"
                    value={formData.description}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="number"
                    name="price"
                    placeholder="Price (Rs. per month)"
                    value={formData.price}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="text"
                    name="location"
                    placeholder="Location"
                    value={formData.location}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="number"
                    name="bedrooms"
                    placeholder="Bedrooms"
                    value={formData.bedrooms}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="number"
                    name="bathrooms"
                    placeholder="Bathrooms"
                    value={formData.bathrooms}
                    onChange={handleInputChange}
                    required
                />
                <label>
                    Furnished:
                    <input
                        type="checkbox"
                        name="furnished"
                        checked={formData.furnished}
                        onChange={handleInputChange}
                    />
                </label>
                <div>
                    Amenities:
                    <label>
                        <input
                            type="checkbox"
                            value="wifi"
                            checked={formData.amenities.includes('wifi')}
                            onChange={handleAmenityChange}
                        />
                        Wi-Fi
                    </label>
                    <label>
                        <input
                            type="checkbox"
                            value="parking"
                            checked={formData.amenities.includes('parking')}
                            onChange={handleAmenityChange}
                        />
                        Parking
                    </label>
                </div>
                <button type="submit">Add Property</button>
            </form>
        </div>
    );
}

export default AddProperty;
