import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { addProperty } from '../services/api';
import './AddProperty.css';

function AddProperty() {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    location: '',
    bedrooms: '',
    bathrooms: '',
    furnished: false,
    amenities: {
      wifi: false,
      parking: false,
      ac: false,
      water: false
    },
    image: null
  });

  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleAmenityChange = (e) => {
    const { name, checked } = e.target;
    setFormData(prevState => ({
      ...prevState,
      amenities: {
        ...prevState.amenities,
        [name]: checked
      }
    }));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setFormData(prevState => ({
      ...prevState,
      image: file
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    const propertyData = new FormData();
    for (const key in formData) {
      if (key === 'amenities') {
        propertyData.append(key, JSON.stringify(formData[key]));
      } else if (key === 'image') {
        if (formData[key]) {
          propertyData.append(key, formData[key]);
        }
      } else {
        propertyData.append(key, formData[key]);
      }
    }

    try {
      console.log('Submitting property data:', Object.fromEntries(propertyData));
      const response = await addProperty(propertyData);
      console.log('Property added successfully:', response.data);
      navigate('/rooms');
    } catch (error) {
      console.error('Error adding property:', error.response ? error.response.data : error.message);
      setError('Failed to add property. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="add-property-page">
      <h1>Add Your Property</h1>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleSubmit} className="add-property-form">
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            required
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="price">Price (per month)</label>
          <input
            type="number"
            id="price"
            name="price"
            value={formData.price}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="location">Location</label>
          <input
            type="text"
            id="location"
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="bedrooms">Bedrooms</label>
          <input
            type="number"
            id="bedrooms"
            name="bedrooms"
            value={formData.bedrooms}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="bathrooms">Bathrooms</label>
          <input
            type="number"
            id="bathrooms"
            name="bathrooms"
            value={formData.bathrooms}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label>
            <input
              type="checkbox"
              name="furnished"
              checked={formData.furnished}
              onChange={handleInputChange}
            />
            Furnished
          </label>
        </div>
        <div className="form-group">
          <label>Amenities</label>
          <div className="amenities-group">
            <label>
              <input
                type="checkbox"
                name="wifi"
                checked={formData.amenities.wifi}
                onChange={handleAmenityChange}
              />
              Wi-Fi
            </label>
            <label>
              <input
                type="checkbox"
                name="parking"
                checked={formData.amenities.parking}
                onChange={handleAmenityChange}
              />
              Parking
            </label>
            <label>
              <input
                type="checkbox"
                name="ac"
                checked={formData.amenities.ac}
                onChange={handleAmenityChange}
              />
              Air Conditioning
            </label>
            <label>
              <input
                type="checkbox"
                name="water"
                checked={formData.amenities.water}
                onChange={handleAmenityChange}
              />
              Water
            </label>
          </div>
        </div>
        <div className="form-group">
          <label htmlFor="image">Property Image</label>
          <input
            type="file"
            id="image"
            name="image"
            onChange={handleImageUpload}
            accept="image/*"
          />
        </div>
        <button type="submit" className="submit-button" disabled={isLoading}>
          {isLoading ? 'Adding Property...' : 'Add Property'}
        </button>
      </form>
    </div>
  );
}

export default AddProperty;
