import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Heart } from 'lucide-react';

const Saved = () => {
  const [savedListings, setSavedListings] = useState([]);

  useEffect(() => {
    // Load favorites from localStorage
    const storedFavorites = localStorage.getItem('favorites');
    if (storedFavorites) {
      setSavedListings(JSON.parse(storedFavorites));
    }
  }, []);

  const removeFavorite = (id) => {
    setSavedListings(prev => {
      const newListings = prev.filter(listing => listing.id !== id);
      localStorage.setItem('favorites', JSON.stringify(newListings));
      return newListings;
    });
  };

  return (
    <div className="saved-page">
      <h1>Saved Listings</h1>
      {savedListings.length === 0 ? (
        <p>You haven't saved any listings yet.</p>
      ) : (
        <ul className="saved-listings-grid">
          {savedListings.map(listing => (
            <li key={listing.id} className="saved-listing-card">
              <img src={listing.image} alt={listing.title} className="saved-listing-image" />
              <div className="saved-listing-details">
                <h3>{listing.title}</h3>
                <p className="saved-listing-price">Rs {listing.price.toLocaleString()}/month</p>
                <p className="saved-listing-furnished">{listing.furnished ? 'Furnished' : 'Unfurnished'}</p>
                <div className="saved-listing-amenities">
                  {listing.amenities.parking && <span className="amenity">üÖøÔ∏è Parking</span>}
                  {listing.amenities.wifi && <span className="amenity">üì∂ WiFi</span>}
                  {listing.amenities.water && <span className="amenity">üíß Water</span>}
                </div>
                <div className="saved-listing-actions">
                  <Link to={`/listing/${listing.id}`} className="btn btn-primary">View Details</Link>
                  <button onClick={() => removeFavorite(listing.id)} className="btn btn-icon btn-remove-favorite">
                    <Heart size={20} fill="red" />
                  </button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Saved;
