import React, { useState } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import { Calendar } from 'lucide-react';

function BookNow() {
  const { id } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const roomDetails = location.state?.roomDetails;

  const [bookingData, setBookingData] = useState({
    checkIn: '',
    checkOut: '',
    guests: 1,
    specialRequests: '',
  });

  if (!roomDetails) {
    // Redirect to home if room details are not available
    navigate('/');
    return null;
  }

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBookingData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically send the booking data to your backend
    console.log('Booking submitted:', { roomId: id, ...bookingData });
    // You could also add a success message or redirect the user
    alert('Booking submitted successfully!');
    navigate('/');
  };

  return (
    <div className="min-h-screen bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-3xl mx-auto bg-white rounded-lg shadow-md overflow-hidden">
        <div className="px-4 py-5 sm:px-6">
          <h2 className="text-2xl font-bold text-gray-900">Book Your Stay</h2>
          <p className="mt-1 text-sm text-gray-600">
            Please fill in the details to complete your booking for {roomDetails.title}.
          </p>
        </div>
        <div className="px-4 py-5 sm:p-6">
          <div className="mb-6">
            <img src={roomDetails.image} alt={roomDetails.title} className="w-full h-64 object-cover rounded-lg" />
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
            <div>
              <h3 className="text-lg font-semibold">{roomDetails.title}</h3>
              <p className="text-gray-600">Price: Rs {roomDetails.price.toLocaleString()}/month</p>
              <p className="text-gray-600">{roomDetails.furnished ? 'Furnished' : 'Unfurnished'}</p>
            </div>
            <div>
              <h4 className="text-md font-semibold mb-2">Amenities:</h4>
              <ul className="list-disc list-inside">
                {roomDetails.amenities.parking && <li>Parking</li>}
                {roomDetails.amenities.wifi && <li>WiFi</li>}
                {roomDetails.amenities.water && <li>Water</li>}
              </ul>
            </div>
          </div>
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label htmlFor="checkIn" className="block text-sm font-medium text-gray-700">
                  Check-in Date
                </label>
                <div className="mt-1 relative rounded-md shadow-sm">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <Calendar className="h-5 w-5 text-gray-400" aria-hidden="true" />
                  </div>
                  <input
                    type="date"
                    name="checkIn"
                    id="checkIn"
                    className="focus:ring-indigo-500 focus:border-indigo-500 block w-full pl-10 sm:text-sm border-gray-300 rounded-md"
                    value={bookingData.checkIn}
                    onChange={handleInputChange}
                    required
                  />
                </div>
              </div>
              <div>
                <label htmlFor="checkOut" className="block text-sm font-medium text-gray-700">
                  Check-out Date
                </label>
                <div className="mt-1 relative rounded-md shadow-sm">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <Calendar className="h-5 w-5 text-gray-400" aria-hidden="true" />
                  </div>
                  <input
                    type="date"
                    name="checkOut"
                    id="checkOut"
                    className="focus:ring-indigo-500 focus:border-indigo-500 block w-full pl-10 sm:text-sm border-gray-300 rounded-md"
                    value={bookingData.checkOut}
                    onChange={handleInputChange}
                    required
                  />
                </div>
              </div>
            </div>
            <div>
              <label htmlFor="guests" className="block text-sm font-medium text-gray-700">
                Number of Guests
              </label>
              <input
                type="number"
                name="guests"
                id="guests"
                min="1"
                className="mt-1 focus:ring-indigo-500 focus:border-indigo-500 block w-full shadow-sm sm:text-sm border-gray-300 rounded-md"
                value={bookingData.guests}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label htmlFor="specialRequests" className="block text-sm font-medium text-gray-700">
                Special Requests
              </label>
              <textarea
                name="specialRequests"
                id="specialRequests"
                rows={3}
                className="mt-1 focus:ring-indigo-500 focus:border-indigo-500 block w-full shadow-sm sm:text-sm border-gray-300 rounded-md"
                value={bookingData.specialRequests}
                onChange={handleInputChange}
              ></textarea>
            </div>
            <div>
              <button
                type="submit"
                className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
              >
                Confirm Booking
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default BookNow;
