import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate, Link, useLocation } from 'react-router-dom';
import { Heart, Wifi, Car, Wind, Droplet } from 'lucide-react';
import { getProperties, getFavorites, addFavorite, removeFavorite } from '../services/api';
import ChatBox from './ChatBox';
import './ViewAllRoom.css';

function ViewAllRooms() {
  const [allRooms, setAllRooms] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [searchParams, setSearchParams] = useState({
    location: '',
    priceRange: '',
    furnished: '',
  });
  const [filteredRooms, setFilteredRooms] = useState([]);
  const [showChat, setShowChat] = useState(false);
  const [currentLandlord, setCurrentLandlord] = useState('');

  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [propertiesResponse, favoritesResponse] = await Promise.all([
          getProperties(),
          getFavorites()
        ]);
        setAllRooms(propertiesResponse.data);
        setFavorites(favoritesResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleSearch = useCallback(() => {
    const filtered = allRooms.filter((room) => {
      const matchesLocation =
        !searchParams.location ||
        room.title.toLowerCase().includes(searchParams.location.toLowerCase());
      const matchesPrice =
        !searchParams.priceRange ||
        (searchParams.priceRange === '0-15000' && room.price <= 15000) ||
        (searchParams.priceRange === '15001-25000' &&
          room.price > 15000 &&
          room.price <= 25000) ||
        (searchParams.priceRange === '25001-35000' &&
          room.price > 25000 &&
          room.price <= 35000) ||
        (searchParams.priceRange === '35001+' && room.price > 35000);
      const matchesFurnished =
        !searchParams.furnished ||
        (searchParams.furnished === 'furnished' && room.furnished) ||
        (searchParams.furnished === 'unfurnished' && !room.furnished);

      return matchesLocation && matchesPrice && matchesFurnished;
    });

    setFilteredRooms(filtered);
  }, [searchParams, allRooms]);

  useEffect(() => {
    // Parse search params from URL
    const params = new URLSearchParams(location.search);
    setSearchParams({
      location: params.get('location') || '',
      priceRange: params.get('priceRange') || '',
      furnished: params.get('furnished') || '',
    });
  }, [location.search]);

  useEffect(() => {
    handleSearch();
  }, [searchParams, handleSearch]);

  const toggleFavorite = async (room) => {
    try {
      if (favorites.some(fav => fav._id === room._id)) {
        await removeFavorite(room._id);
        setFavorites(prev => prev.filter(fav => fav._id !== room._id));
      } else {
        await addFavorite(room._id);
        setFavorites(prev => [...prev, room]);
      }
    } catch (error) {
      console.error('Error toggling favorite:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchParams((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleBookNow = (room) => {
    navigate(`/booknow/${room._id}`, { state: { roomDetails: room } });
  };

  const handleChatWithLandlord = (landlordName) => {
    setCurrentLandlord(landlordName);
    setShowChat(true);
  };

  return (
    <div className="view-all-rooms-page">
      <div className="container">
        <Link to="/" className="btn btn-secondary back-to-home">
          Back to Home
        </Link>
        <h1>All Available Rooms</h1>

        <div className="search-form">
          <input
            type="text"
            name="location"
            placeholder="Search by location"
            value={searchParams.location}
            onChange={handleInputChange}
          />
          <select
            name="priceRange"
            value={searchParams.priceRange}
            onChange={handleInputChange}
          >
            <option value="">Price Range</option>
            <option value="0-15000">Rs 0 - Rs 15,000</option>
            <option value="15001-25000">Rs 15,001 - Rs 25,000</option>
            <option value="25001-35000">Rs 25,001 - Rs 35,000</option>
            <option value="35001+">Rs 35,001+</option>
          </select>
          <select
            name="furnished"
            value={searchParams.furnished}
            onChange={handleInputChange}
          >
            <option value="">Furnished Status</option>
            <option value="furnished">Furnished</option>
            <option value="unfurnished">Unfurnished</option>
          </select>
          <button className="btn btn-primary" onClick={handleSearch}>
            Search
          </button>
        </div>

        <div className="listings-grid">
          {filteredRooms.map((room) => (
            <div key={room._id} className="listing-card">
              <img
                src={room.image}
                alt={room.title}
                className="listing-image"
              />
              <div className="listing-details">
                <h3>{room.title}</h3>
                <p className="listing-price">Rs {room.price.toLocaleString()}/month</p>
                <p className="furnished-status">
                  {room.furnished ? 'Furnished' : 'Unfurnished'}
                </p>
                <div className="amenities">
                  {room.amenities.wifi && <div className="amenity"><Wifi size={16} /> Wi-Fi</div>}
                  {room.amenities.parking && <div className="amenity"><Car size={16} /> Parking</div>}
                  {room.amenities.ac && <div className="amenity"><Wind size={16} /> AC</div>}
                  {room.amenities.water && <div className="amenity"><Droplet size={16} /> Water</div>}
                </div>
                <div className="listing-actions">
                  <button
                    className="btn btn-primary btn-book"
                    onClick={() => handleBookNow(room)}
                  >
                    Book Now
                  </button>
                  <button
                    className="btn btn-outline btn-chat"
                    onClick={() => handleChatWithLandlord(room.landlord.name)}
                  >
                    💬 Chat with Landlord
                  </button>
                  <button
                    className={`btn btn-icon ${
                      favorites.some(fav => fav._id === room._id) ? 'btn-favorite-active' : ''
                    }`}
                    onClick={() => toggleFavorite(room)}
                  >
                    <Heart size={20} fill={favorites.some(fav => fav._id === room._id) ? "red" : "none"} />
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      {showChat && (
        <ChatBox
          landlordName={currentLandlord}
          onClose={() => setShowChat(false)}
        />
      )}
    </div>
  );
}

export default ViewAllRooms;
