// AddProperty.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { addProperty } from '../services/api';
import './AddProperty.css';

function AddProperty({ onPropertyAdded }) {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    location: '',
    bedrooms: '',
    bathrooms: '',
    furnished: false,
    amenities: {
      wifi: false,
      parking: false,
      ac: false,
      water: false
    },
    image: null
  });

  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);
  
    const propertyData = new FormData();
    for (const key in formData) {
      if (key === 'amenities') {
        propertyData.append(key, JSON.stringify(formData[key]));
      } else if (key === 'image') {
        if (formData[key]) {
          propertyData.append(key, formData[key]);
        }
      } else {
        propertyData.append(key, formData[key]);
      }
    }
  
    console.log('Sending property data:', [...propertyData.entries()]); // Debug log
  
    try {
      const response = await addProperty(propertyData);
  
      // Check if the response status is 201
      if (response.status === 201) {
        console.log('Property added successfully:', response.data); // Debug log
        onPropertyAdded(response.data); // Update the state with the new property
        navigate('/rooms'); // Redirect to the room list page
      } else {
        throw new Error('Unexpected response status');
      }
    } catch (error) {
      console.error('Error Adding Property:', error.response ? error.response.data : error.message);
      setError('Failed to add property. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };
  

  return (
    <div className="add-property-page">
      <h1>Add Your Property</h1>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleSubmit} className="add-property-form">
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={(e) => setFormData({ ...formData, title: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            required
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="price">Price (per month)</label>
          <input
            type="number"
            id="price"
            name="price"
            value={formData.price}
            onChange={(e) => setFormData({ ...formData, price: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="location">Location</label>
          <input
            type="text"
            id="location"
            name="location"
            value={formData.location}
            onChange={(e) => setFormData({ ...formData, location: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="bedrooms">Bedrooms</label>
          <input
            type="number"
            id="bedrooms"
            name="bedrooms"
            value={formData.bedrooms}
            onChange={(e) => setFormData({ ...formData, bedrooms: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="bathrooms">Bathrooms</label>
          <input
            type="number"
            id="bathrooms"
            name="bathrooms"
            value={formData.bathrooms}
            onChange={(e) => setFormData({ ...formData, bathrooms: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <label>
            <input
              type="checkbox"
              name="furnished"
              checked={formData.furnished}
              onChange={(e) => setFormData({ ...formData, furnished: e.target.checked })}
            />
            Furnished
          </label>
        </div>
        <div className="form-group">
          <label>Amenities</label>
          <div className="amenities-group">
            <label>
              <input
                type="checkbox"
                name="wifi"
                checked={formData.amenities.wifi}
                onChange={(e) => setFormData({
                  ...formData,
                  amenities: { ...formData.amenities, wifi: e.target.checked }
                })}
              />
              Wi-Fi
            </label>
            <label>
              <input
                type="checkbox"
                name="parking"
                checked={formData.amenities.parking}
                onChange={(e) => setFormData({
                  ...formData,
                  amenities: { ...formData.amenities, parking: e.target.checked }
                })}
              />
              Parking
            </label>
            <label>
              <input
                type="checkbox"
                name="ac"
                checked={formData.amenities.ac}
                onChange={(e) => setFormData({
                  ...formData,
                  amenities: { ...formData.amenities, ac: e.target.checked }
                })}
              />
              Air Conditioning
            </label>
            <label>
              <input
                type="checkbox"
                name="water"
                checked={formData.amenities.water}
                onChange={(e) => setFormData({
                  ...formData,
                  amenities: { ...formData.amenities, water: e.target.checked }
                })}
              />
              Water
            </label>
          </div>
        </div>
        <div className="form-group">
          <label htmlFor="image">Property Image</label>
          <input
            type="file"
            id="image"
            name="image"
            onChange={(e) => setFormData({ ...formData, image: e.target.files[0] })}
            accept="image/*"
          />
        </div>
        <button type="submit" className="submit-button" disabled={isLoading}>
          {isLoading ? 'Adding Property...' : 'Add Property'}
        </button>
      </form>
    </div>
  );
}

export default AddProperty;
