import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Star, Bell, MessageCircle, Heart, User, Car, Wifi, Droplets, Search } from 'lucide-react';
import './Home.css';
import './RentalMap.css';
import ChatBox from './ChatBox';
import RentalMap from './RentalMap';

const featuredListings = [
  {
    id: 1,
    title: "Spacious room in Thamel",
    price: 25000,
    location: "Thamel, Kathmandu",
    furnished: true,
    type: "Studio",
    amenities: {
      parking: true,
      wifi: true,
      water: true
    },
    image: "/room1.jpg",
    landlordName: "Nifiya Shrestha"
  },
  {
    id: 2,
    title: "Spacious Apartment in Patan",
    price: 35000,
    location: "Patan, Lalitpur",
    furnished: false,
    type: "Apartment",
    amenities: {
      parking: true,
      wifi: true,
      water: false
    },
    image: "/room2.jpeg",
    landlordName: "Nitika Suwal"
  },
  {
    id: 3,
    title: "Modern Loft in Lazimpat",
    price: 30000,
    location: "Lazimpat, Kathmandu",
    furnished: true,
    type: "Loft",
    amenities: {
      parking: false,
      wifi: true,
      water: true
    },
    image: "/room3.jpg",
    landlordName: "Sara Lamichhane"
  }
];

function Home() {
  const [showDropdown, setShowDropdown] = useState(false);
  const [showChat, setShowChat] = useState(false);
  const [currentLandlord, setCurrentLandlord] = useState('');
  const [favorites, setFavorites] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (!event.target.closest('.user-menu')) {
        setShowDropdown(false);
      }
    };

    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const handleLogout = () => {
    console.log('User logged out');
    navigate('/login');
  };

  const toggleFavorite = (roomId) => {
    setFavorites(prevFavorites => 
      prevFavorites.includes(roomId)
        ? prevFavorites.filter(id => id !== roomId)
        : [...prevFavorites, roomId]
    );
  };

  const handleBookNow = (room) => {
    console.log('Booking room:', room);
    navigate(`/book/${room.id}`);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    navigate(`/rooms?search=${searchQuery}`);
  };

  return (
    <div className="home-page">
      <header className="main-header">
        <div className="container">
          <div className="logo">
            <Link to="/">RoomRental</Link>
          </div>
          <nav className="main-nav">
            <ul>
              <li><Link to="/rooms">Find Rooms</Link></li>
              <li><Link to="/add-property">List Your Property</Link></li>
            </ul>
          </nav>
          <div className="user-menu">
            <button className="notifications">
              <Bell size={20} />
            </button>
            <button className="messages">
              <MessageCircle size={20} />
            </button>
            <button className="user-profile" onClick={toggleDropdown}>
              <User size={20} />
            </button>
            {showDropdown && (
              <div className="dropdown-menu">
                <Link to="/profile">Profile</Link>
                <Link to="/settings">Settings</Link>
                <button onClick={handleLogout}>Logout</button>
              </div>
            )}
          </div>
        </div>
      </header>

      <section className="hero">
        <div className="container">
          <h1>Find Your Perfect Room</h1>
          <p>Discover comfortable and affordable rooms for rent in Nepal</p>
          <form onSubmit={handleSearch} className="search-form">
            <input
              type="text"
              placeholder="Search by location, price, or amenities"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit">Search</button>
          </form>
        </div>
      </section>

      <section className="featured-section">
        <div className="container">
          <h1 className="featured-title">Featured Rooms</h1>
          <div className="featured-grid">
            {featuredListings.map((room) => (
              <div key={room.id} className="room-card">
                <div className="room-image-wrapper">
                  <img src={room.image} alt={room.title} className="room-image" />
                </div>
                <div className="room-details">
                  <h2 className="room-title">{room.title}</h2>
                  <p className="room-location">{room.location}</p>
                  <p className="room-price">Rs {room.price.toLocaleString()}/month</p>
                  <div className="room-status">
                    <span className="status-badge">{room.furnished ? 'Furnished' : 'Unfurnished'}</span>
                    <span className="type-badge">{room.type}</span>
                  </div>
                  <div className="amenities-list">
                    {room.amenities.parking && (
                      <div className="amenity-item">
                        <Car className="amenity-icon" />
                        <span>Parking</span>
                      </div>
                    )}
                    {room.amenities.wifi && (
                      <div className="amenity-item">
                        <Wifi className="amenity-icon" />
                        <span>WiFi</span>
                      </div>
                    )}
                    {room.amenities.water && (
                      <div className="amenity-item">
                        <Droplets className="amenity-icon" />
                        <span>Water</span>
                      </div>
                    )}
                  </div>
                  <div className="room-actions">
                    <button 
                      className="book-now-btn"
                      onClick={() => handleBookNow(room)}
                    >
                      Book Now
                    </button>
                    <div className="secondary-actions">
                      <button 
                        className="chat-btn"
                        onClick={() => {
                          setCurrentLandlord(room.landlordName);
                          setShowChat(true);
                        }}
                      >
                        <MessageCircle className="btn-icon" />
                        Chat with Landlord
                      </button>
                      <button 
                        className={`favorite-btn ${favorites.includes(room.id) ? 'favorited' : ''}`}
                        onClick={() => toggleFavorite(room.id)}
                      >
                        <Heart className="btn-icon" />
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="view-all-container">
            <Link to="/rooms" className="view-all-btn">View All Rooms</Link>
          </div>
        </div>
      </section>

      <section className="how-it-works">
        <div className="container">
          <h2>How It Works</h2>
          <div className="steps">
            <div className="step">
              <div className="step-icon">
                <Search size={32} />
              </div>
              <h3>Search</h3>
              <p>Find the perfect room that fits your needs and budget.</p>
            </div>
            <div className="step">
              <div className="step-icon">
                <MessageCircle size={32} />
              </div>
              <h3>Connect</h3>
              <p>Chat with landlords and schedule viewings.</p>
            </div>
            <div className="step">
              <div className="step-icon">
                <Star size={32} />
              </div>
              <h3>Book</h3>
              <p>Secure your room and move in with ease.</p>
            </div>
          </div>
        </div>
      </section>

      <section className="map-section">
        <div className="container">
          <h2>Explore Rooms by Location</h2>
          <RentalMap />
        </div>
      </section>

      <footer className="main-footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h3>About Us</h3>
              <p>RoomRental is your trusted platform for finding and renting rooms in Nepal.</p>
            </div>
            <div className="footer-section">
              <h3>Quick Links</h3>
              <ul>
                <li><Link to="/about">About</Link></li>
                <li><Link to="/contact">Contact</Link></li>
                <li><Link to="/terms">Terms of Service</Link></li>
                <li><Link to="/privacy">Privacy Policy</Link></li>
              </ul>
            </div>
            <div className="footer-section">
              <h3>Contact Us</h3>
              <p>Email: info@roomrental.com</p>
              <p>Phone: +977 1234567890</p>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2023 RoomRental. All rights reserved.</p>
          </div>
        </div>
      </footer>

      {showChat && (
        <ChatBox
          landlordName={currentLandlord}
          onClose={() => setShowChat(false)}
        />
      )}
    </div>
  );
}

export default Home;
