import React, { useState } from 'react';
import { Bell } from 'lucide-react';

const mockNotifications = [
  { id: 1, message: 'New message from landlord', read: false },
  { id: 2, message: 'Booking request approved', read: false },
  { id: 3, message: 'New property listed in your area', read: true },
];

const NotificationButton = () => {
  const [notifications, setNotifications] = useState(mockNotifications);
  const [showNotifications, setShowNotifications] = useState(false);

  const unreadCount = notifications.filter(n => !n.read).length;

  const toggleNotifications = () => {
    setShowNotifications(!showNotifications);
  };

  const markAsRead = (id) => {
    setNotifications(notifications.map(n => 
      n.id === id ? { ...n, read: true } : n
    ));
  };

  return (
    <div className="notification-button-container">
      <button onClick={toggleNotifications} className="notification-button">
        <Bell size={20} />
        {unreadCount > 0 && <span className="notification-badge">{unreadCount}</span>}
      </button>
      {showNotifications && (
        <div className="notification-dropdown">
          <h3>Notifications</h3>
          {notifications.length === 0 ? (
            <p>No new notifications</p>
          ) : (
            <ul>
              {notifications.map(notification => (
                <li key={notification.id} className={notification.read ? 'read' : 'unread'}>
                  {notification.message}
                  {!notification.read && (
                    <button onClick={() => markAsRead(notification.id)}>Mark as read</button>
                  )}
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
};

export default NotificationButton;
