import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Star, Bell, MessageCircle, Heart, User, Car, Wifi, Droplets, Search, MapPin } from 'lucide-react';
import './ViewAllRooms.css';
import ChatBox from './ChatBox';

const allRooms = [
  {
    id: 1,
    title: "Cozy Studio in Thamel",
    price: 25000,
    location: "Thamel, Kathmandu",
    furnished: true,
    type: "Studio",
    amenities: { parking: true, wifi: true, water: true },
    image: "/room1.jpg",
    landlordName: "Aarav Sharma"
  },
  {
    id: 2,
    title: "Spacious 2BHK in Patan",
    price: 35000,
    location: "Patan, Lalitpur",
    furnished: false,
    type: "Apartment",
    amenities: { parking: true, wifi: true, water: false },
    image: "/room2.jpeg",
    landlordName: "Priya Thapa"
  },
  {
    id: 3,
    title: "Modern Loft in Lazimpat",
    price: 30000,
    location: "Lazimpat, Kathmandu",
    furnished: true,
    type: "Loft",
    amenities: { parking: false, wifi: true, water: true },
    image: "/room3.jpg",
    landlordName: "Bikash Rai"
  },
  {
    id: 4,
    title: "Family Home in Bhaktapur",
    price: 40000,
    location: "Bhaktapur",
    furnished: true,
    type: "House",
    amenities: { parking: true, wifi: true, water: true },
    image: "/room4.jpg",
    landlordName: "Sarita Maharjan"
  },
  {
    id: 5,
    title: "Student Apartment near Tribhuvan University",
    price: 20000,
    location: "Kirtipur, Kathmandu",
    furnished: false,
    type: "Apartment",
    amenities: { parking: false, wifi: true, water: true },
    image: "/room5.jpg",
    landlordName: "Rajesh Shrestha"
  }
];

export default function ViewAllRooms() {
  const [rooms, setRooms] = useState(allRooms);
  const [filters, setFilters] = useState({
    location: '',
    priceRange: '',
    furnished: '',
    type: '',
    amenities: []
  });
  const [favorites, setFavorites] = useState([]);
  const [showChat, setShowChat] = useState(false);
  const [currentLandlord, setCurrentLandlord] = useState('');
  const [showDropdown, setShowDropdown] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (token && storedUser) {
      setIsLoggedIn(true);
      setUsername(storedUser.name);
    } else {
      setIsLoggedIn(false);
    }
  }, []);

  const handleFilterChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFilters(prevFilters => ({
      ...prevFilters,
      [name]: type === 'checkbox' 
        ? checked 
          ? [...prevFilters.amenities, value]
          : prevFilters.amenities.filter(item => item !== value)
        : value
    }));
  };

  const applyFilters = () => {
    let filteredRooms = allRooms.filter(room => {
      return (
        (filters.location === '' || room.location.toLowerCase().includes(filters.location.toLowerCase())) &&
        (filters.priceRange === '' || (
          (filters.priceRange === '0-20000' && room.price <= 20000) ||
          (filters.priceRange === '20001-30000' && room.price > 20000 && room.price <= 30000) ||
          (filters.priceRange === '30001-40000' && room.price > 30000 && room.price <= 40000) ||
          (filters.priceRange === '40001+' && room.price > 40000)
        )) &&
        (filters.furnished === '' || room.furnished.toString() === filters.furnished) &&
        (filters.type === '' || room.type === filters.type) &&
        (filters.amenities.length === 0 || filters.amenities.every(amenity => room.amenities[amenity]))
      );
    });
    setRooms(filteredRooms);
  };

  const toggleFavorite = (roomId) => {
    setFavorites(prevFavorites => 
      prevFavorites.includes(roomId)
        ? prevFavorites.filter(id => id !== roomId)
        : [...prevFavorites, roomId]
    );
  };

  const handleBookNow = (room) => {
    navigate(`/book/${room.id}`);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsLoggedIn(false);
    navigate('/');
  };

  return (
    <div className="view-all-rooms">
      <header className="main-header">
        <div className="container">
          <div className="logo">
            <Link to="/">RoomRental</Link>
          </div>
          <nav className="main-nav">
            <ul>
              <li><Link to="/rooms">Find Rooms</Link></li>
              <li><Link to="/add-property">List Your Property</Link></li>
            </ul>
          </nav>
          <div className="user-menu">
            <button className="notifications">
              <Bell size={20} />
            </button>
            <button className="messages">
              <MessageCircle size={20} />
            </button>
            <button className="user-profile" onClick={() => setShowDropdown(!showDropdown)}>
              <User size={20} />
            </button>
            {showDropdown && (
              <div className="dropdown-menu">
                <Link to="/profile">Profile</Link>
                <Link to="/settings">Settings</Link>
                <button onClick={handleLogout}>Logout</button>
              </div>
            )}
          </div>
        </div>
      </header>

      <section className="hero">
        <div className="container">
          <h1>Find Your Perfect Room</h1>
          <p>Discover comfortable and affordable rooms for rent in Nepal</p>
          <form onSubmit={(e) => { e.preventDefault(); applyFilters(); }} className="search-form">
            <input
              type="text"
              name="location"
              placeholder="Where do you want to live?"
              value={filters.location}
              onChange={handleFilterChange}
            />
            <select
              name="priceRange"
              value={filters.priceRange}
              onChange={handleFilterChange}
            >
              <option value="">Price Range</option>
              <option value="0-20000">Rs 0 - Rs 20,000</option>
              <option value="20001-30000">Rs 20,001 - Rs 30,000</option>
              <option value="30001-40000">Rs 30,001 - Rs 40,000</option>
              <option value="40001+">Rs 40,001+</option>
            </select>
            <select
              name="furnished"
              value={filters.furnished}
              onChange={handleFilterChange}
            >
              <option value="">Furnished Status</option>
              <option value="true">Furnished</option>
              <option value="false">Unfurnished</option>
            </select>
            <select
              name="type"
              value={filters.type}
              onChange={handleFilterChange}
            >
              <option value="">Room Type</option>
              <option value="Studio">Studio</option>
              <option value="Apartment">Apartment</option>
              <option value="House">House</option>
              <option value="Loft">Loft</option>
            </select>
            <button type="submit" className="btn btn-primary">Search Rooms</button>
          </form>
        </div>
      </section>

      <section className="featured-listings">
        <div className="container">
          <h2 className="section-title">Available Rooms</h2>
          <div className="rooms-grid">
            {rooms.map((room) => (
              <div key={room.id} className="room-card">
                <div className="room-image-container">
                  <img src={room.image} alt={room.title} className="room-image" />
                  <button 
                    className={`favorite-btn ${favorites.includes(room.id) ? 'favorited' : ''}`}
                    onClick={() => toggleFavorite(room.id)}
                  >
                    <Heart size={20} />
                  </button>
                  <div className="room-type-badge">{room.type}</div>
                </div>
                <div className="room-content">
                  <h3 className="room-title">{room.title}</h3>
                  <p className="room-location">
                    <MapPin size={16} />
                    {room.location}
                  </p>
                  <p className="room-price">Rs {room.price.toLocaleString()}<span>/month</span></p>
                  <div className="room-features">
                    <span className={`feature-badge ${room.furnished ? 'furnished' : 'unfurnished'}`}>
                      {room.furnished ? 'Furnished' : 'Unfurnished'}
                    </span>
                    <div className="amenities">
                      {room.amenities.parking && <Car size={16} />}
                      {room.amenities.wifi && <Wifi size={16} />}
                      {room.amenities.water && <Droplets size={16} />}
                    </div>
                  </div>
                  <div className="room-actions">
                    <button className="btn-primary" onClick={() => handleBookNow(room)}>Book Now</button>
                    <button className="btn-secondary" onClick={() => {
                      setCurrentLandlord(room.landlordName);
                      setShowChat(true);
                    }}>
                      Chat with Landlord
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      <footer className="main-footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h3>About Us</h3>
              <p>RoomRental is your trusted platform for finding and renting rooms in Nepal.</p>
            </div>
            <div className="footer-section">
              <h3>Quick Links</h3>
              <ul>
                <li><Link to="/about">About</Link></li>
                <li><Link to="/contact">Contact</Link></li>
                <li><Link to="/terms">Terms of Service</Link></li>
                <li><Link to="/privacy">Privacy Policy</Link></li>
              </ul>
            </div>
            <div className="footer-section">
              <h3>Contact Us</h3>
              <p>Email: info@roomrental.com</p>
              <p>Phone: +977 1234567890</p>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2023 RoomRental. All rights reserved.</p>
          </div>
        </div>
      </footer>

      {showChat && (
        <ChatBox
          landlordName={currentLandlord}
          onClose={() => setShowChat(false)}
        />
      )}
    </div>
  );
}
